import { useState } from "react"
import { useNavigate } from "react-router-dom"

const MainForm = () => {

    let navigate = useNavigate();

    const [error, setError] = useState("")
    const [data, setData] = useState({ name:"", room:"" })

    const handleChange = e => {
        setData({
            ...data, 
            [e.target.name]: e.target.value
        })
    }

    const validation = () => {
        if(!data.name){ 
            setError("Please enter your name.")
            return false
        }
        if(!data.room){ 
            setError("Please select room.")
            return false
        } 
        setError("")
        return true
    }

    const handleSubmit = e => {
        e.preventDefault()
        const isValid = validation()
        if(isValid){
            navigate(`/chat/${data.room}`, { state: data });
        }
    }

    return (<div>
        <nav class="navbar navbar-light bg-light">
            <span class="navbar-brand mb-0 h1">Navbar</span>
        </nav>
        <div className="px-3 py-4 shadow text-dark border rounded h-50 row bg-dark">
            <h1 className="text-warning display-1">We all are humans</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group mb-4">
                    <h2 className="text-warning mb-4">Welcome to MyTypes</h2>
                </div>
                <div className="form-group mb-4">
                    <input type="name" className="form-control bg-light" name="name" placeholder="Enter name" onChange={handleChange} />
                </div>
                <div className="form-group mb-4">
                    <select className="form-select bg-light" name="room" aria-label="Default select example" onChange={handleChange}>
                        <option value="">Select Room</option>
                        <option value="vitiligo">Vitiligo</option>
                        <option value="Baldness">Baldness</option>
                        <option value="Rosacea">Rosacea</option>
                    </select>
                </div>
                <button type="submit" className="btn btn-warning w-100 mb-2">Submit</button>
                {error ? <small className="text-danger m-auto">{error}</small> : "" }
            </form>
        </div>
        </div>
    )
}

export default MainForm
